    -fx-font-size: 15px;
    -fx-pref-width: 180px;
    -fx-pref-height: 40px;
    -fx-background-radius: 10px;
    -fx-alignment: CENTER_LEFT;
    -fx-font-weight: 600;
    -fx-padding: 12 18;
    -fx-graphic-text-gap: 12;
    -fx-cursor: hand;
